select id, `name`, teacher_in_charge, `type`, core, nature, credit, coach, coach_teacher, colleges_or_departments, examination_way, research_room, assessment_way, text_book, reference_book, homework_proportion, test_proportion, lab_proportion, attendance_proportion, performance_proportion, paper_proportion, other_proportion, exam_proportion, time_week, week_num, time_theory, time_practical, time_computer, time_lab, time_homework, time_all, theory_week, `status`, book_manager from course
id=#{courseId}
and `name` like concat ('%',#{courseName},'%')
and nature =#{nature}
and type =#{type}
and research_room=#{researchRoom}
and teacher_in_charge =#{teacherInCharge}
and JSON_LENGTH(text_book)>0
and JSON_LENGTH(text_book)=0
and `status`=0
order by id

select teacher.name as tea_name, course.book_manager as tea_id
from course
       join teacher on course.book_manager = teacher.id
where course.id = #{id}
  and course.`status` = 0;
